import React, { useState } from "react";
import PhoneInput from "react-phone-input-2";
import 'react-phone-input-2/lib/style.css'
import "./phone.css"
import { MdTextFields } from "react-icons/md";
import { GiSmallFire } from "react-icons/gi";
import { RecaptchaVerifier } from "firebase/auth/web-extension";
import { auth } from "./setup";
import { signInWithPhoneNumber } from "firebase/auth";


 function PhoneSignin() {

    const [phone,setPhone] = useState("")
    const [user, setUser] = useState("")
    const [otp, setOtp] =useState ("")

    const sendOtp =async()=>{
        try{
            const recaptcha = new RecaptchaVerifier(auth, "recaptcha",{})
            const confirmation = signInWithPhoneNumber(auth,phone,recaptcha)
            setUser(confirmation)
        }catch(err){
            console.error(err)
        }
      
    }

    const verifyOtp=async()=>{
        try{
            const data = await user.confirm(otp)
            console.log(data)
        }catch(err)
        {
            console.error(err) 
        }
        
        
    }

    return (
        <div className="phone-signin">
            <div className="phone-content">
            <PhoneInput
                country={'us'}
                 value={phone}
                 onChange={(phone)=>setPhone("+"  + phone  )}
            />
        <button onClick={sendOtp} sx= {{marginTop:"10px"}}   variant='contained '>Send OTP </button>
        <div style={{marginTop:"10px"}}  id=" recaptcha">

        </div>
        <br/>

        <MdTextFields onChange={(e)=> setOtp(e.target.value)}  sx={{marginTop:"10px" , width:"300px"}} variant ="outlined" size={GiSmallFire} aria-label="Enter OTP "/>
        <br/>
            <button onClick={verifyOtp} sx={{marginTop:"10px"}}   variant="contained" color="success ">Verify OTP</button>
            </div>
            
        </div> 
 ) 
}

export default PhoneSignin
