import React, { useRef } from 'react';
import { Download, Save } from 'lucide-react';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import './Prescription.css';

const Prescription = ({ symptoms, remedies, medications }) => {
  const prescriptionRef = useRef(null);
  const currentDate = new Date().toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  // Convert first letter to uppercase
  const formatSymptom = (symptom) => {
    if (!symptom) return '';
    return symptom.charAt(0).toUpperCase() + symptom.slice(1);
  };

  const handleDownload = () => {
    const prescriptionElement = prescriptionRef.current;
    
    html2canvas(prescriptionElement, {
      // Improve quality and handling of prescription content
      scale: 2,
      useCORS: true,
      allowTaint: true,
      backgroundColor: '#ffffff'
    }).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = pdf.internal.pageSize.getHeight();
      const imgWidth = canvas.width;
      const imgHeight = canvas.height;
      const ratio = Math.min(pdfWidth / imgWidth, pdfHeight / imgHeight);
      const imgX = (pdfWidth - imgWidth * ratio) / 2;
      const imgY = 30;
      
      pdf.addImage(imgData, 'PNG', imgX, imgY, imgWidth * ratio, imgHeight * ratio);
      pdf.save(`MEDICONNECT_Prescription_${formatSymptom(symptoms)}.pdf`);
    });
  };

  const handleSave = () => {
    // Implementation for saving to cloud/account would go here
    alert('Prescription saved to your account');
  };

  return (
    <div className="prescription-container">
      {/* Create an inner container for PDF capture */}
      <div className="prescription-pdf-content" ref={prescriptionRef}>
        <div className="prescription-header">
          <h2 className="prescription-title">MEDICONNECT Prescription</h2>
          <p className="prescription-subtitle">Your personalized health recommendation</p>
        </div>
        
        <div className="prescription-date">{currentDate}</div>
        
        <div className="prescription-content">
          <div className="prescription-section">
            <h3 className="prescription-section-title">Symptoms</h3>
            <ul className="prescription-list">
              <li className="prescription-list-item">{formatSymptom(symptoms)}</li>
            </ul>
          </div>
          
          {remedies && remedies.length > 0 && (
            <div className="prescription-section">
              <h3 className="prescription-section-title">Recommended Remedies</h3>
              <ul className="prescription-list">
                {remedies.map((remedy, index) => (
                  <li key={index} className="prescription-list-item">{remedy}</li>
                ))}
              </ul>
            </div>
          )}
          
          {medications && medications.length > 0 && (
            <div className="prescription-section">
              <h3 className="prescription-section-title">Recommended Medications</h3>
              <div>
                {medications.map((medication, index) => (
                  <div key={index} className="medication-item">
                    <div className="medication-name">{medication.name}</div>
                    <div className="medication-dosage">Dosage: {medication.dosage}</div>
                  </div>
                ))}
              </div>
            </div>
          )}
          
          <div className="prescription-footer">
            <p>Note: This is a general recommendation. Please consult with a healthcare professional before starting any medication.</p>
          </div>
        </div>
      </div>
      
      {/* Keep buttons outside the PDF capture area but still in the component */}
      <div className="prescription-actions">
        <button className="prescription-button download-button" onClick={handleDownload}>
          <Download size={18} />
          Download PDF
        </button>
        <button className="prescription-button save-button" onClick={handleSave}>
          <Save size={18} />
          Save Prescription
        </button>
      </div>
    </div>
  );
};

export default Prescription;
