import React , {useEffect, useState} from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";

function Navbar() {
  const[user, setUser] = useState(false)

  useEffect(()=>{
    let user = window.localStorage.getItem("user");
    console.log(user)
    if(user){
      setUser(true)
    }

  },[])

  return (
    <nav className="navbar">
      <Link to="/" className="navbar-logo">
        <img src="/Logo.png" alt="Medi Connect Logo" className="navbar-logo-image" />
      </Link>
      <ul className="navbar-links">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/about">About</Link></li>
        <li><Link to="/services">Services</Link></li>
        <li><a href="https://esanjeevani.mohfw.gov.in/#/">Sanjeevani App</a></li>
        { !user && <li><Link to="/login">Login</Link></li> }       
        { user && <li><Link to="/chatbot">Chatbot</Link></li> }
        { user && <li><Link to="/video-conferencing">Video Conferencing</Link></li> }
        { user && <li><Link to="/asha-login">Asha Worker</Link></li> }
        { user && (
          <li>
            <button
              onClick={() => {
                window.localStorage.removeItem("user");
                setUser(false);
                window.location.href = "/login";
              }}
            >
              Logout
            </button>
          </li>
        )}
      </ul>
    </nav>
  );
}  

export default Navbar;
