import React, { useEffect, useState } from 'react';
import { jsPDF } from "jspdf";
import { Download, FilePlus, Printer } from 'lucide-react';
import './Prescription.css';

const Prescription = ({ symptoms, remedies, medications }) => {
  const [showPrescription, setShowPrescription] = useState(false);
  
  // Don't steal focus when component mounts
  useEffect(() => {
    const chatInput = document.querySelector('.input-form input');
    if (chatInput) {
      setTimeout(() => chatInput.focus(), 100);
    }
  }, []);

  const generatePDF = (e) => {
    // Prevent focus issues when button is clicked
    e.preventDefault();
    
    const doc = new jsPDF();

    // Header Section
    doc.setFontSize(16);
    doc.setFont("helvetica", "bold");
    doc.setTextColor(0, 102, 204); // Blue header color
    doc.text("AI Assisted Telemedicine Kiosk for Rural India", 10, 20);
    doc.setFontSize(14);
    doc.setFont("helvetica", "italic");
    doc.setTextColor(0, 0, 0); // Black text
    doc.text("Team Mediconnect", 10, 30);

    // Divider Line
    doc.setDrawColor(0, 102, 204); // Blue line
    doc.line(10, 35, 200, 35);

    let currentY = 45; // Starting y-coordinate for content

    // Symptoms Section
    doc.setFontSize(14);
    doc.setFont("helvetica", "bold");
    doc.setTextColor(0, 51, 102); // Dark blue for section headers
    doc.text("Symptoms:", 10, currentY);
    currentY += 10;
    doc.setFont("helvetica", "normal");
    doc.setTextColor(0, 0, 0); // Black text for content
    doc.text(`• ${symptoms || "No symptoms provided"}`, 10, currentY);
    currentY += 20;

    // Remedies Section
    doc.setFontSize(14);
    doc.setFont("helvetica", "bold");
    doc.setTextColor(0, 51, 102); // Dark blue for section headers
    doc.text("Suggested Remedies:", 10, currentY);
    currentY += 10;
    (remedies || []).forEach((remedy) => {
      doc.setFont("helvetica", "normal");
      doc.setTextColor(0, 0, 0); // Black text for content
      doc.text(`• ${remedy}`, 10, currentY);
      currentY += 10;
    });
    currentY += 10;

    // Medications Section
    doc.setFontSize(14);
    doc.setFont("helvetica", "bold");
    doc.setTextColor(0, 51, 102); // Dark blue for section headers
    doc.text("Suggested Medications:", 10, currentY);
    currentY += 10;

    // Check if medications are passed and valid
    if (medications && Array.isArray(medications) && medications.length > 0) {
      medications.forEach((medication) => {
        const medicationName = medication?.name || "Unknown Medication";
        const medicationDosage = medication?.dosage || "As directed";
        doc.setFont("helvetica", "normal");
        doc.setTextColor(0, 0, 0); // Black text for content
        doc.text(`• ${medicationName} - ${medicationDosage}`, 10, currentY);
        currentY += 10;
      });
    } else {
      doc.setFont("helvetica", "italic");
      doc.setFontSize(12); // Slightly smaller font size for the placeholder
      doc.setTextColor(120, 120, 120); // Light gray text for distinction
      doc.text("No medications provided.", 10, currentY);
    }

    // Footer Section
    doc.setDrawColor(0, 102, 204); // Blue footer line
    doc.line(10, 270, 200, 270);
    doc.setFontSize(12);
    doc.setFont("helvetica", "italic");
    doc.setTextColor(0, 0, 0);
    doc.text("Generated by Team MediConnect", 10, 280);

    // Save the PDF file
    doc.save(`Prescription_${symptoms || "Medical"}.pdf`);
    
    // Restore focus to chat input after generating PDF
    setTimeout(() => {
      const chatInput = document.querySelector('.input-form input');
      if (chatInput) {
        chatInput.focus();
      }
    }, 100);
  };

  const handleShowPrescription = () => {
    setShowPrescription(true);
  };

  const handlePrint = () => {
    window.print();
  };

  return (
    <div className="prescription-container">
      {!showPrescription ? (
        <button 
          onClick={handleShowPrescription} 
          className="view-prescription-button"
          onMouseDown={(e) => e.preventDefault()} // Prevent focus stealing on mousedown
        >
          <FilePlus size={18} />
          View Prescription
        </button>
      ) : (
        <>
          <div className="prescription-card">
            <div className="prescription-header">
              <h2>Medical Prescription</h2>
              <p>Generated by MEDICONNECT AI Assistant</p>
            </div>
            
            <div className="prescription-content">
              <div className="prescription-section">
                <h3>Symptoms</h3>
                <div className="highlight-box">
                  <p>{symptoms || "No symptoms provided"}</p>
                </div>
              </div>
              
              <div className="prescription-section">
                <h3>Recommended Remedies</h3>
                <ul className="remedy-list">
                  {remedies && remedies.map((remedy, index) => (
                    <li key={index}>{remedy}</li>
                  ))}
                  {(!remedies || remedies.length === 0) && (
                    <li className="no-data">No remedies available</li>
                  )}
                </ul>
              </div>
              
              <div className="prescription-section">
                <h3>Recommended Medications</h3>
                <ul className="medication-list">
                  {medications && medications.map((medication, index) => (
                    <li key={index}>
                      <span className="medication-name">{medication.name}</span>
                      <span className="medication-dosage">{medication.dosage}</span>
                    </li>
                  ))}
                  {(!medications || medications.length === 0) && (
                    <li className="no-data">No medications recommended</li>
                  )}
                </ul>
              </div>
              
              <div className="prescription-footer">
                <p><strong>Note:</strong> This prescription is generated based on your symptoms and conversation with our AI assistant. Please consult with a healthcare professional before starting any medication.</p>
              </div>
            </div>
            
            <div className="prescription-actions">
              <button 
                onClick={generatePDF} 
                className="action-button download-button"
                onMouseDown={(e) => e.preventDefault()}
              >
                <Download size={16} />
                Download PDF
              </button>
              
              <button 
                onClick={handlePrint}
                className="action-button print-button"
                onMouseDown={(e) => e.preventDefault()}
              >
                <Printer size={16} />
                Print
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Prescription;
