import axios from 'axios';

const API_KEY = 'YOUR_YOUTUBE_API_KEY'; // Replace with your actual API key

export async function fetchYouTubeRecommendations(searchQuery) {
    const languages = ['hi', 'mr', 'en']; // Hindi, Marathi, and English
    const maxResults = 6; // Set the maximum results you want

    const fetchVideos = async (lang) => {
        // Use a dynamic query based on the symptom
        const query = `${searchQuery} treatment ${lang === 'hi' ? 'हिन्दी' : lang === 'mr' ? 'मराठी' : ''}`;
        const URL = `https://www.googleapis.com/youtube/v3/search?part=snippet&q=${encodeURIComponent(query)}&type=video&key=${API_KEY}&maxResults=${maxResults}&relevanceLanguage=${lang}`;
        
        try {
            const response = await axios.get(URL);
            return response.data.items;
        } catch (error) {
            console.error("Error fetching YouTube videos", error);
            return [];
        }
    };

    // Fetch videos for Hindi, Marathi, and English
    const hindiVideos = await fetchVideos('hi');
    const marathiVideos = await fetchVideos('mr');
    const englishVideos = await fetchVideos('en');

    // Combine results and remove duplicates if necessary
    const allVideos = [...hindiVideos, ...marathiVideos, ...englishVideos];

    // Limit to 6 unique videos
    const uniqueVideos = Array.from(new Set(allVideos.map(video => video.id.videoId)))
        .slice(0, maxResults)
        .map(id => allVideos.find(video => video.id.videoId === id));

    return uniqueVideos;
}
