import React, { useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";


const HomePage = () => {
  const [roomNo, setRoomNo] = useState("");

  const navigate = useNavigate();

  const sendVerificationEmail = async (e,) => {
    e.preventDefault();
    setIsLoading(true);
    setMessage('');
    setError('');

    const otp = generateOTP();

    const templateParams = {
      to_email: email,
      otp: otp,
      // Add any other template parameters you need
    };

    try {
      // Replace these with your Email.js credentials
      await emailjs.send(
        'YOUR_SERVICE_ID',
        'YOUR_TEMPLATE_ID',
        templateParams,
        'YOUR_PUBLIC_KEY'
      );

      setMessage('Verification email sent successfully!');
      // You might want to store the OTP in localStorage or state for verification
      localStorage.setItem('emailOTP', otp.toString());
    } catch (err) {
      setError('Failed to send email. Please try again.');
      console.error('Error sending email:', err);
    } finally {
      setIsLoading(false);
    }
  };
  const handleJoinRoom = useCallback(() => {
    const roomID=localStorage.getItem('roomID');
    if (roomNo.trim()) {
      
      navigate(`/room/${roomNo.trim()}`);
    } else {
      alert("Please enter a valid Room ID.");
    }
  }, [navigate, roomNo]);

  return (
    <div style={styles.wrapper}>
      <img
        src="https://media.istockphoto.com/id/1263326624/vector/video-conferencing-at-home-close-up-african-young-woman-having-video-call-meeting-with.jpg?s=612x612&w=0&k=20&c=bgggESPzhZdCPe42a-AbJWApDetOrQ9ZD7hT4WbZlFo="
        alt="Room Illustration"
        style={styles.image}
      />
      <div style={styles.container}>
        <span style={styles.text}>Enter Room ID to join</span>
        <div style={styles.inputGroup}>
          <input
            value={roomNo}
            onChange={(e) => setRoomNo(e.target.value)}
            type="text"
            placeholder="Enter Room Code"
            style={styles.input}
          />
          <button onClick={handleJoinRoom} style={styles.button}>
            Join
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  wrapper: {
    textAlign: "center",
  },
  image: {
    width: "500px",
    marginBottom: "50px",
  },
  container: {
    backgroundColor: "#hjf562",
    padding: "40px",
    borderRadius: "12px",
    boxShadow: "0 8px 15px rgba(0, 0, 0, 0.1)",
    maxWidth: "400px",
    margin: "0 auto",
  },
  text: {
    fontSize: "1.2rem",
    fontWeight: "600",
    color: "#333",
    marginBottom: "10px",
  },
  inputGroup: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    gap: "10px",
  },
  input: {
    
    padding: "10px",
    border: "2px solid #ddd",
    borderRadius: "8px",
    fontSize: "1rem",
  },
  button: {
    backgroundColor: "#007bff",
    color: "white",
    padding: "10px 20px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    textTransform: "uppercase",
    transition: "all 0.3s ease",
  },
};

export default HomePage;
