import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";

function Navbar() {
  const [user, setUser] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    let user = window.localStorage.getItem("user");
    if(user) {
      setUser(true);
    }

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <nav className={`navbar ${isScrolled ? 'scroll-nav' : ''}`}>
      <Link to="/" className="navbar-logo">
        <img src="/Logo.png" alt="Medi Connect Logo" className="navbar-logo-image" />
      </Link>
      <ul className="navbar-links">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/about">About</Link></li>
        <li><Link to="/services">Services</Link></li>
        <li>
          <a 
            href="https://esanjeevani.mohfw.gov.in/#/" 
            target="_blank" 
            rel="noopener noreferrer"
          >
            Sanjeevani App
          </a>
        </li>
        {!user && <li><Link to="/login">Login</Link></li>}
        {user && (
          <>
            <li><Link to="/chatbot">Chatbot</Link></li>
            <li><Link to="/video-conferencing">Video Conferencing</Link></li>
            <li><Link to="/asha-login">Asha Worker</Link></li>
            <li>
              <button
                className="logout-btn"
                onClick={() => {
                  window.localStorage.removeItem("user");
                  setUser(false);
                  window.location.href = "/login";
                }}
              >
                Logout
              </button>
            </li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
