import React, { useState } from "react";
import {
  Box,
  Button,
  Container,
  FormControl,
  // FormLabel,
  Input,
  Stack,
  Text,
  Heading,
  useToast,
  Image,
  InputGroup,
  InputRightElement,
} from "@chakra-ui/react";
import { signInWithEmailAndPassword } from "firebase/auth";
import styled from "styled-components";
import { getDatabase, ref, get } from "firebase/database";
import { auth } from "../../firebaseConfig";
import { useNavigate } from "react-router-dom";
import img from "../../assets/Authentication/img.gif";
import backgroundImage from "../../assets/loginsignup.png"
import showeye from "../../assets/12.png";
import hideeye from "../../assets/13.png";


function LoginPage({ setUser }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const toast = useToast();
  const handleClick = () => setShow(!show);
  const [show, setShow] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
  
     
  
      get(userRef)
        .then((snapshot) => {
          if (snapshot.exists()) {
            const userData = snapshot.val();
            setUser({
              ...user,
              firstName: userData.firstName,
              lastName: userData.lastName,
            });
          } else {
            console.log("No user data found");
          }
        })
        .catch((error) => {
          console.error("Error fetching user data:", error);
        });
  
      navigate("/input"); // Redirect to the input page after login
    } catch (error) {
      toast({
        title: "Login failed.",
        description: "Please check your email and password.",
        status: "error",
        duration: 5000,
        isClosable: true,
      });
    }
  };
  

  return (
    <Section >
     < Container maxW="md" centerContent>
          <Box
            size="xl"
            align="center"
            padding={10}
            display="flex"
            // boxShadow="2xl"
            borderRadius={40}
            position="fixed"
            top="120px"
            right="60px"
            left="150px"
            height={600}
            width={500}
            bgColor="white"
          >
            <Stack>
              <Heading
                size="2xl"
                pl="50px"
                mb={5}
                mt={10}
                color="#1e147e"
                align="center"
              >
                Login
              </Heading>
              <FormControl pl="30px" id="email">
                <Text fontSize="xl" mb={5}>
                  Login for accesing all the features
                </Text>
                <Input
                  mb={5}
                  h="50px"
                  w="350px"
                  type="email"
                  placeholder="Enter your email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  isRequired
                />
              </FormControl>
              <FormControl pl="30px" id="password">
                <InputGroup size="md">
                  <Input
                    mb={7}
                    h="50px"
                    w="350px"
                    type={show ? "text" : "password"}
                    placeholder="Enter your password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    isRequired
                  />
                  <InputRightElement pt="10px" width="4.5rem">
                    <Image src={show ? showeye :  hideeye }
                                        h="23px"
                                        cursor="pointer"
                                        onClick={handleClick}/>
                  </InputRightElement>
                </InputGroup>
                <Button
                  mb={5}
                  h="50px"
                  w="350px"
                  bg="blue.600"
                  color="white"
                  _hover={{ bg: "blue.700" }}
                  fontSize="16px"
                  onClick={handleLogin}
                >
                  Login
                </Button>
              </FormControl>
              <Text pl="30px" mt={4}>
                Don't have an account?{" "}
                <Button variant="link" onClick={() => navigate("/signup")}>
                  Sign Up
                </Button>
              </Text>
            </Stack>
          </Box>
        </Container>
         </Section>
  );
}

export default LoginPage;
const Section = styled.div`
  display: flex;
  height: 100vh;
  padding: 20px;
  margin-left: 650px;
  box-sizing: border-box;
  background-image: url(${backgroundImage});
  background-repeat: no-repeat;
`;

const LeftSection = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const RightSection = styled.div`
  flex: 1;
  display: flex;
  position: fixed
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;
const Img = styled.img`
  width: 80%;
  height: 80%;
  object-fit: cover;
`;
