import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";
import { FaBars, FaTimes } from "react-icons/fa";

function Navbar() {
  const [user, setUser] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    
    let user = window.localStorage.getItem("user");
    if(user) {
      setUser(true);
    }

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
      <Link to="/" className="navbar-logo">
        <img src="/Logo.png" alt="Medi Connect Logo" className="navbar-logo-image" />
      </Link>

      <button className="menu-button" onClick={toggleMenu}>
        {isMenuOpen ? <FaTimes /> : <FaBars />}
      </button>

      <ul className={`navbar-links ${isMenuOpen ? 'active' : ''}`}>
        <li><Link to="/" onClick={() => setIsMenuOpen(false)}>Home</Link></li>
        <li><Link to="/about" onClick={() => setIsMenuOpen(false)}>About</Link></li>
        <li><Link to="/services" onClick={() => setIsMenuOpen(false)}>Services</Link></li>
        <li>
          <a 
            href="https://esanjeevani.mohfw.gov.in/#/" 
            target="_blank" 
            rel="noopener noreferrer"
            onClick={() => setIsMenuOpen(false)}
          >
            Sanjeevani App
          </a>
        </li>
        
        {!user && (
          <li><Link to="/login" onClick={() => setIsMenuOpen(false)}>Login</Link></li>
        )}
        
        {user && (
          <>
            <li><Link to="/chatbot" onClick={() => setIsMenuOpen(false)}>Chatbot</Link></li>
            <li><Link to="/video-conferencing" onClick={() => setIsMenuOpen(false)}>Video Conferencing</Link></li>
            <li><Link to="/asha-login" onClick={() => setIsMenuOpen(false)}>Asha Worker</Link></li>
            <li>
              <button
                className="chatbot-button"
                onClick={() => {
                  window.localStorage.removeItem("user");
                  setUser(false);
                  window.location.href = "/login";
                  setIsMenuOpen(false);
                }}
              >
                Logout
              </button>
            </li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
