import React, { createContext, useState, useContext } from "react";

const PrescriptionContext = createContext();

export const usePrescription = () => {
  return useContext(PrescriptionContext);
};

 const PrescriptionProvider = ({ children }) => {
  const [prescription, setPrescription] = useState(null);

  const updatePrescription = (newPrescription) => {
    setPrescription(newPrescription);
  };

  return (
    <PrescriptionContext.Provider value={{ prescription, updatePrescription }}>
      {children}
    </PrescriptionContext.Provider>
  );
};

export default PrescriptionProvider;
