import React, { useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import { 
  FaAmbulance, FaHospital, FaMobileAlt, FaRobot, 
  FaVideo, FaStethoscope, FaChevronLeft, FaChevronRight
} from "react-icons/fa";
import './Home.css';

function Home() {
  const testimonialsRef = useRef(null);
  const [pauseScroll, setPauseScroll] = useState(false);

  useEffect(() => {
    // Simple animation for features
    const features = document.querySelectorAll('.feature-card');
    features.forEach((feature, index) => {
      feature.style.opacity = '0';
      feature.style.transform = 'translateY(20px)';
      setTimeout(() => {
        feature.style.transition = 'opacity 0.6s ease, transform 0.6s ease';
        feature.style.opacity = '1';
        feature.style.transform = 'translateY(0)';
      }, 200 + index * 100);
    });

    // Improved testimonial scrolling
    if (testimonialsRef.current) {
      let animationFrameId;
      let scrollSpeed = 0.5; // Reduced speed
      let isHovering = false;

      const handleMouseEnter = () => {
        isHovering = true;
      };
      
      const handleMouseLeave = () => {
        isHovering = false;
      };

      testimonialsRef.current.addEventListener('mouseenter', handleMouseEnter);
      testimonialsRef.current.addEventListener('mouseleave', handleMouseLeave);

      const scroll = () => {
        if (testimonialsRef.current && !isHovering && !pauseScroll) {
          testimonialsRef.current.scrollLeft += scrollSpeed;
          
          // Smoother reset when reaching the end
          const maxScroll = testimonialsRef.current.scrollWidth - testimonialsRef.current.clientWidth;
          if (testimonialsRef.current.scrollLeft >= maxScroll) {
            testimonialsRef.current.scrollLeft = 0;
          }
        }
        animationFrameId = requestAnimationFrame(scroll);
      };

      animationFrameId = requestAnimationFrame(scroll);

      return () => {
        cancelAnimationFrame(animationFrameId);
        if (testimonialsRef.current) {
          testimonialsRef.current.removeEventListener('mouseenter', handleMouseEnter);
          testimonialsRef.current.removeEventListener('mouseleave', handleMouseLeave);
        }
      };
    }
  }, [pauseScroll]);

  const scrollTestimonials = (direction) => {
    if (testimonialsRef.current) {
      const cardWidth = 340; // Approximate width of a card + gap
      const scrollAmount = direction * cardWidth;
      testimonialsRef.current.scrollBy({ left: scrollAmount, behavior: 'smooth' });
      
      // Pause auto-scroll briefly after manual scroll
      setPauseScroll(true);
      setTimeout(() => setPauseScroll(false), 3000);
    }
  };

  return (
    <div className="home-container">
      <section className="hero-section">
        <div className="hero-background"></div>
        <div className="hero-content">
          <div className="hero-flex">
            <div className="hero-text">
              <h1 className="intro-text">
                CONNECTING RURAL INDIA TO MODERN HEALTHCARE
              </h1>
              <p className="hero-description">
                MediConnect is revolutionizing healthcare accessibility in rural India through 
                innovative technology solutions. Our AI-powered platform connects patients with 
                doctors, provides instant medical advice, and ensures timely assistance even in 
                the most remote areas.
              </p>
              <div className="hero-cta">
                <Link to="/about">
                  <button className="cta-button">Learn More</button>
                </Link>
                <Link to="/services">
                  <button className="cta-button secondary">Our Services</button>
                </Link>
              </div>
            </div>
            <div className="hero-image-container">
              <img src="/Chatbot.png" alt="MediConnect AI Chatbot" className="hero-image" />
            </div>
          </div>
        </div>
      </section>

      <section className="ai-highlight-section">
        <div className="ai-highlight-content">
          <div className="ai-highlight-info">
            <h2 className="section-title no-after">AI-Powered Healthcare</h2>
            <p className="ai-highlight-description">
              Our advanced AI technology understands multiple Indian languages, identifies symptoms,
              and provides immediate guidance. Available 24/7, it bridges the gap between rural
              communities and quality healthcare.
            </p>
            <Link to="/chatbot">
              <button className="cta-button dark">Try Our Chatbot</button>
            </Link>
          </div>
          <div className="ai-highlight-image">
            <img src="/Chatbot.png" alt="AI Chatbot Interface" className="bounce-animation" />
          </div>
        </div>
      </section>

      <section className="features-section">
        <h2 className="section-title">Our Solutions</h2>
        <div className="features-grid">
          <div className="feature-card">
            <FaMobileAlt className="feature-icon" />
            <h3 className="feature-title">Mobile-Based Access</h3>
            <p className="feature-description">
              Access healthcare services directly from your mobile phone using simple OTP authentication,
              making healthcare available to everyone with a basic phone.
            </p>
          </div>

          <div className="feature-card">
            <FaRobot className="feature-icon" />
            <h3 className="feature-title">AI-Powered Chatbot</h3>
            <p className="feature-description">
              Our multilingual AI chatbot guides patients through the process and provides 
              recommendations based on symptoms in the language they're comfortable with.
            </p>
          </div>

          <div className="feature-card">
            <FaVideo className="feature-icon" />
            <h3 className="feature-title">Remote Consultations</h3>
            <p className="feature-description">
              Video conferencing allows patients to consult with specialists from their local kiosk 
              or home, eliminating the need for long-distance travel.
            </p>
          </div>

          <div className="feature-card">
            <FaHospital className="feature-icon" />
            <h3 className="feature-title">Access to Specialists</h3>
            <p className="feature-description">
              Get referred to specialists and receive treatment plans through a simple and 
              efficient process from the comfort of your village.
            </p>
          </div>

          <div className="feature-card">
            <FaStethoscope className="feature-icon" />
            <h3 className="feature-title">Digital Prescriptions</h3>
            <p className="feature-description">
              Receive and store digital prescriptions that can be accessed anytime and used 
              at local pharmacies for easier medication access.
            </p>
          </div>

          <div className="feature-card">
            <FaAmbulance className="feature-icon" />
            <h3 className="feature-title">Emergency Services</h3>
            <p className="feature-description">
              Quick access to ambulance services during emergencies with live tracking and 
              estimated arrival times for better emergency management.
            </p>
          </div>
        </div>
      </section>

      <section className="testimonials-section">
        <div className="testimonials-container">
          <h2 className="section-title">Success Stories</h2>
          <div className="testimonials-wrapper">
            <button 
              className="testimonial-nav-btn prev-btn" 
              onClick={() => scrollTestimonials(-1)}
              aria-label="Previous testimonial"
            >
              <FaChevronLeft />
            </button>
            <div className="testimonials-grid" ref={testimonialsRef}>
              <div className="testimonial-card">
                <p className="testimonial-quote">
                  "MediConnect helped my grandmother get specialist consultation without traveling 
                  100km to the city. The doctor diagnosed her condition through video call and prescribed 
                  medication that was delivered to our doorstep."
                </p>
                <div className="testimonial-author">Rajesh Kumar</div>
                <div className="testimonial-role">Villager, Madhya Pradesh</div>
              </div>

              <div className="testimonial-card">
                <p className="testimonial-quote">
                  "As an ASHA worker, this platform has made my job significantly easier. I can now 
                  connect villagers with doctors instantly and maintain digital health records for everyone."
                </p>
                <div className="testimonial-author">Lakshmi Devi</div>
                <div className="testimonial-role">ASHA Worker, Tamil Nadu</div>
              </div>

              <div className="testimonial-card">
                <p className="testimonial-quote">
                  "The AI chatbot quickly identified my symptoms as potential dengue fever and 
                  arranged an immediate video consultation with a doctor. This prompt action probably 
                  saved my life."
                </p>
                <div className="testimonial-author">Amit Singh</div>
                <div className="testimonial-role">Farmer, Uttar Pradesh</div>
              </div>

              <div className="testimonial-card">
                <p className="testimonial-quote">
                  "Being able to speak to the AI in my native language made all the difference. I could 
                  explain my symptoms properly and get the right medical advice."
                </p>
                <div className="testimonial-author">Sarita Patel</div>
                <div className="testimonial-role">Homemaker, Gujarat</div>
              </div>
            </div>
            <button 
              className="testimonial-nav-btn next-btn" 
              onClick={() => scrollTestimonials(1)}
              aria-label="Next testimonial"
            >
              <FaChevronRight />
            </button>
          </div>
          <div className="testimonial-indicators">
            <span className="indicator active"></span>
            <span className="indicator"></span>
            <span className="indicator"></span>
            <span className="indicator"></span>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Home;
