import { jsPDF } from "jspdf";

const Prescription = ({ symptoms, remedies, medications, patientName }) => {
  const generatePDF = () => {
    const doc = new jsPDF();

    
    doc.setFontSize(20);
    doc.text("MediConnect - Prescription", 10, 20);

  
    doc.setFontSize(12);
    doc.text(`Symptoms: ${symptoms || "N/A"}`, 10, 40);

    
    doc.text("Suggested Remedies:", 10, 70);
    (remedies || []).forEach((remedy, index) => {
      doc.text(`${index + 1}. ${remedy}`, 10, 80 + index * 10);
    });

  
    doc.text("Suggested Medications:", 10, 120);
    (medications || []).forEach((medication, index) => {
      const medicationName = medication?.name || "Unknown Medication";
      const medicationDosage = medication?.dosage || "As directed";
      doc.text(
        `${index + 1}. ${medicationName} - ${medicationDosage}`,
        10,
        130 + index * 10
      );
    });

  
    doc.setFontSize(20);
    doc.text("Generated by Team MediConnect", 10, 280);

    // Save the PDF
    doc.save(`Prescription.pdf`);
  };

  return (
    <div>
      <button onClick={generatePDF} className="generate-pdf-btn">
        Download & Save Prescription
      </button>
    </div>
  );
};

export default Prescription;
