import { jsPDF } from "jspdf";

const Prescription = ({ symptoms, remedies, medications }) => {
  const generatePDF = () => {
    const doc = new jsPDF();

    // Header Section: Team name & Telemedicine kiosk
    doc.setFontSize(16);
    doc.setFont("helvetica", "bold");
    doc.text("AI Assisted Telemedicine Kiosk for Rural India", 10, 20);
    doc.setFont("helvetica", "normal");
    doc.text("Team Mediconnect", 10, 30);

    // Symptoms Section
    doc.setFontSize(14);
    doc.setFont("helvetica", "bold");
    doc.text("Symptoms:", 10, 50);
    doc.setFont("helvetica", "normal");
    doc.text(`• ${symptoms || "No symptoms provided"}`, 10, 60);

    // Remedies Section
    doc.setFontSize(14);
    doc.setFont("helvetica", "bold");
    doc.text("Suggested Remedies:", 10, 80);
    (remedies || []).forEach((remedy, index) => {
      doc.setFont("helvetica", "normal");
      doc.text(`• ${remedy}`, 10, 90 + index * 10);
    });

    // Medications Section
    doc.setFontSize(14);
    doc.setFont("helvetica", "bold");
    doc.text("Suggested Medications:", 10, 110);
    (medications || []).forEach((medication, index) => {
      const medicationName = medication?.name || "Unknown Medication";
      const medicationDosage = medication?.dosage || "As directed";
      doc.setFont("helvetica", "normal");
      doc.text(
        `• ${medicationName} - ${medicationDosage}`,
        10,
        120 + index * 10
      );
    });

    // Footer Section: Generated by Team Mediconnect
    doc.setFontSize(12);
    doc.setFont("helvetica", "italic");
    doc.text("Generated by Team MediConnect", 10, 280);

    // Save the PDF file
    doc.save(`Prescription.pdf`);
  };

  return (
    <div>
      <button onClick={generatePDF} className="generate-pdf-btn" style={buttonStyle}>
        Download & Save Prescription
      </button>
    </div>
  );
};

// Custom button style for download
const buttonStyle = {
  backgroundColor: "#007bff",
  color: "white",
  border: "none",
  padding: "10px 20px",
  fontSize: "16px",
  cursor: "pointer",
  borderRadius: "5px",
};

export default Prescription;
