import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [passwordError, setPasswordError] = useState(false); // Toggle for error message display
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    if (username === 'Mediconnect' && password === 'Mediconnect') {
      setPasswordError(false); 
      navigate('/chatbot'); // Redirect to Chatbot page on successful login
    } else {
      setPasswordError(true); // Show error only if credentials are incorrect on submit
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <label>Username</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label>Password</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)} // Update password without triggering error
          required
        />
        {passwordError && <p className="error-message">Invalid credentials. Please try again.</p>} {/* Error message */}
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
