import { jsPDF } from "jspdf";
import { useContext } from "react";
import { PrescriptionContext } from "../context/PrescriptionContext"; // Import context

const Prescription = ({ symptoms, remedies, medications, patientName }) => {
  const { addPrescription } = useContext(PrescriptionContext); // Access global state

  const generatePDF = () => {
    const doc = new jsPDF();
    
    doc.setFontSize(20);
    doc.text("MediConnect - Prescription", 10, 20);

    doc.setFontSize(12);
    doc.text(`Patient: ${patientName}`, 10, 30);
    doc.text(`Symptoms: ${symptoms}`, 10, 40);
    
    doc.text("Suggested Remedies:", 10, 70);
    remedies.forEach((remedy, index) => {
      doc.text(`${index + 1}. ${remedy}`, 10, 80 + index * 10);
    });

    doc.text("Suggested Medications:", 10, 120);
    medications.forEach((medication, index) => {
      doc.text(`${index + 1}. ${medication.name} - ${medication.dosage || "As directed"}`, 10, 130 + index * 10);
    });

    doc.setFontSize(20);
    doc.text("Generated by Team MediConnect", 10, 280);
    doc.save(`Prescription.pdf`);

    // Store in global state
    const newPrescription = {
      id: Date.now(),
      patient: patientName,
      prescription: medications.map(med => `${med.name} - ${med.dosage}`).join(", ")
    };
    addPrescription(newPrescription);
  };

  return (
    <div>
      <button onClick={generatePDF} className="generate-pdf-btn">
        Download & Save Prescription
      </button>
    </div>
  );
};

export default Prescription;
