const MEDICONNECTChatbot = () => {
  const [language, setLanguage] = useState("english");
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSendMessage = () => {
    const trimmedInput = input.trim();
    if (trimmedInput) {
      const newMessages = [...messages, { sender: "user", text: trimmedInput }];
      setMessages(newMessages);
      setInput("");

      const userMessage = trimmedInput.toLowerCase();
      const response = generateResponse(userMessage);
      if (response) {
        setTimeout(() => {
          setMessages((prev) => [...prev, { sender: "bot", text: response }]);
        }, 1000);
      }
    }
  };

  const generateResponse = (message) => {
    // User greeting
    if (message.includes("hello") || message.includes("hi")) {
      return languages[language].welcome;
    }

    // User thanks
    if (message.includes("thank you") || message.includes("thanks")) {
      return languages[language].thankYou;
    }

    // Identify medical issues
    const medicalIssues = Object.keys(medicalQA[language]);
    for (let issue of medicalIssues) {
      if (message.includes(issue)) {
        const remedies = medicalQA[language][issue].remedies;
        const medications = medicalQA[language][issue].medications;

        let response = `${languages[language].recommendRemedies}\n- ${remedies.join("\n- ")}\n\n`;
        response += `${languages[language].recommendMedication}\n- ${medications.map(med => `${med.name}: ${med.dosage}`).join("\n- ")}`;
        return response;
      }
    }

    return languages[language].notFound;
  };

  const handleClearChat = () => {
    setMessages([]);
  };

  return (
    <div className="chatbot-container">
      <div className="chat-window">
        <div className="chat-header">
          <h2>MEDICONNECT Chatbot</h2>
          <button onClick={handleClearChat}>{languages[language].clearChat}</button>
        </div>
        <div className="chat-body">
          {messages.map((msg, index) => (
            <div key={index} className={msg.sender}>
              <span>{msg.text}</span>
            </div>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            value={input}
            onChange={handleInputChange}
            placeholder={languages[language].placeholder}
          />
          <button onClick={handleSendMessage}><Send /></button>
        </div>
      </div>
      <VideoRecommendations />
      <div className="chat-disclaimer">
        <p>{languages[language].disclaimer}</p>
      </div>
    </div>
  );
};

export default MEDICONNECTChatbot;
