import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const AshaWorkerLogin = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  // Define valid Asha Worker credentials
  const validAshaWorkers = [
    { email: "ashaworker123@gmail.com", password: "Mediconnect" },
    { email: "asha2@example.com", password: "password2" },
  ];

  const handleLogin = (e) => {
    e.preventDefault();

    // Check if the email and password match any valid Asha Worker
    const isValid = validAshaWorkers.some(
      (worker) => worker.email === email && worker.password === password
    );

    if (isValid) {
      // Redirect to the dashboard if credentials are valid
      navigate("/asha-dashboard");
    } else {
      // Show an error message if credentials are invalid
      alert("Invalid email or password. Please try again.");
    }
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        minHeight: "100vh",
        backgroundImage: "url('https://via.placeholder.com/1920x1080?text=Healthcare+Background')", // Add a background image
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <div
        style={{
          backgroundColor: "rgba(255, 255, 255, 0.9)", // Semi-transparent white background
          padding: "2rem",
          borderRadius: "10px",
          boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
          width: "400px",
          textAlign: "center",
        }}
      >
        <h2
          style={{
            fontSize: "2rem",
            fontWeight: "bold",
            marginBottom: "1.5rem",
            color: "#2d3748", // Dark gray color
          }}
        >
          Asha Worker Login
        </h2>
        <form onSubmit={handleLogin}>
          <div style={{ marginBottom: "1rem" }}>
            <label
              style={{
                display: "block",
                fontSize: "0.875rem",
                fontWeight: "medium",
                marginBottom: "0.5rem",
                color: "#4a5568", // Gray color
              }}
            >
              Email
            </label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={{
                width: "100%",
                padding: "0.5rem",
                border: "1px solid #e2e8f0", // Light gray border
                borderRadius: "0.375rem",
                outline: "none",
                transition: "border-color 0.2s",
              }}
              required
            />
          </div>
          <div style={{ marginBottom: "1.5rem" }}>
            <label
              style={{
                display: "block",
                fontSize: "0.875rem",
                fontWeight: "medium",
                marginBottom: "0.5rem",
                color: "#4a5568", // Gray color
              }}
            >
              Password
            </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={{
                width: "100%",
                padding: "0.5rem",
                border: "1px solid #e2e8f0", // Light gray border
                borderRadius: "0.375rem",
                outline: "none",
                transition: "border-color 0.2s",
              }}
              required
            />
          </div>
          <button
            type="submit"
            style={{
              width: "100%",
              backgroundColor: "#4299e1", // Blue color
              color: "white",
              padding: "0.5rem",
              borderRadius: "0.375rem",
              fontWeight: "bold",
              cursor: "pointer",
              transition: "background-color 0.2s",
              border: "none",
            }}
            onMouseOver={(e) => (e.target.style.backgroundColor = "#3182ce")} // Darker blue on hover
            onMouseOut={(e) => (e.target.style.backgroundColor = "#4299e1")} // Original blue on mouse out
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default AshaWorkerLogin;
