function MEDICONNECTChatbot() {
  const [chatHistory, setChatHistory] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [language, setLanguage] = useState("english");
  const [loading, setLoading] = useState(false);
  const [showRecommendations, setShowRecommendations] = useState(false);
  const [recommendationData, setRecommendationData] = useState([]);
  
  const handleSend = () => {
    if (userInput.trim() === "") return;
    const newChatHistory = [...chatHistory, { type: "user", message: userInput }];
    setChatHistory(newChatHistory);
    
    // Simulating a delay for the chatbot response
    setLoading(true);
    setTimeout(() => {
      const response = getChatbotResponse(userInput.trim().toLowerCase());
      const updatedChatHistory = [...newChatHistory, { type: "bot", message: response }];
      setChatHistory(updatedChatHistory);
      setUserInput("");
      setLoading(false);
      if (response.includes("recommendation")) {
        setShowRecommendations(true);
      }
    }, 1000);
  };

  const getChatbotResponse = (input) => {
    // Logic to determine chatbot response
    let response;
    if (input.includes("hello") || input.includes("hi")) {
      response = languages[language].welcome;
    } else if (input.includes("symptoms")) {
      response = languages[language].askMedicine;
    } else if (input.includes("medication")) {
      response = languages[language].askWhichMedicine;
    } else if (input.includes("duration")) {
      response = languages[language].askDuration;
    } else if (input.includes("recommend")) {
      response = languages[language].recommendRemedies;
    } else if (input.includes("thank you")) {
      response = languages[language].thankYou;
    } else {
      response = languages[language].notFound;
    }
    
    return response;
  };

  const handleClearChat = () => {
    setChatHistory([]);
    setShowRecommendations(false);
    setRecommendationData([]);
  };

  const handleLanguageChange = (event) => {
    setLanguage(event.target.value);
    setChatHistory([]);
  };

  return (
    <div className="chatbot-container">
      <h1>MEDICONNECT Chatbot</h1>
      <select onChange={handleLanguageChange} value={language}>
        <option value="english">English</option>
        <option value="hindi">हिंदी</option>
        <option value="marathi">मराठी</option>
      </select>
      <div className="chat-window">
        {chatHistory.map((chat, index) => (
          <div key={index} className={chat.type}>
            {chat.message}
          </div>
        ))}
        {loading && <div className="loading">Loading...</div>}
      </div>
      <div className="chat-input">
        <input
          type="text"
          placeholder={languages[language].placeholder}
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && handleSend()}
        />
        <button onClick={handleSend} disabled={loading}>
          <Send />
        </button>
        <button onClick={handleClearChat}>
          <Trash2 />
        </button>
      </div>
      {showRecommendations && <VideoRecommendations recommendations={recommendationData} />}
      <div className="disclaimer">{languages[language].disclaimer}</div>
    </div>
  );
}

export default MEDICONNECTChatbot;
