import { jsPDF } from "jspdf";

const Prescription = ({ symptoms, remedies, medications }) => {
  const generatePDF = () => {
    const doc = new jsPDF();

    

    // Add Prescription Header
    doc.setFontSize(20);
    doc.text("MediConnect - Prescription", 10, 20);

    // Patient Details
    doc.setFontSize(12);
    doc.text(`Symptoms: ${symptoms}`, 10, 40);

    // Remedies
    doc.text("Suggested Remedies:", 10, 70);
    remedies.forEach((remedy, index) => {
      doc.text(`${index + 1}. ${remedy}`, 10, 80 + index * 10);
    });

    // Medications
    doc.text("Suggested Medications:", 10, 120);
    medications.forEach((medication, index) => {
      doc.text(`${index + 1}. ${medication.name} - ${medication.dosage || "As directed"}`, 10, 130 + index * 10);
    });

    // Footer
    doc.setFontSize(10);
    doc.text("Generated by MediConnect", 10, 280);

    // Save PDF
    doc.save(`Prescription.pdf`);
  };

  return (
    <div>
      <button onClick={generatePDF} className="generate-pdf-btn">
        Download Prescription
      </button>
    </div>
  );
};

export default Prescription;
