import axios from 'axios';

const API_KEY = 'AIzaSyAKlTOimxiUY4mCFlulFLmXFYlt5x6TOr8';

export async function fetchYouTubeRecommendations(searchQuery) {
    const languages = ['hi']; // Only Hindi
    const maxResults = 6;

    const fetchVideos = async (lang) => {
        const query = `${searchQuery} treatment`;
        const URL = `https://www.googleapis.com/youtube/v3/search?part=snippet&q=${encodeURIComponent(query)}&type=video&key=${API_KEY}&maxResults=10&relevanceLanguage=${lang}`;
        
        try {
            const response = await axios.get(URL);
            return response.data.items;
        } catch (error) {
            console.error("Error fetching YouTube videos", error);
            return [];
        }
    };

    const hindiVideos = await fetchVideos('hi');

    const uniqueVideos = Array.from(new Set(hindiVideos.map(video => video.id.videoId)))
        .map(id => hindiVideos.find(video => video.id.videoId === id));

    const shuffledVideos = uniqueVideos.sort(() => Math.random() - 0.5);

    return shuffledVideos.slice(0, maxResults);
}
