const Chatbot = () => {
  const [userMessage, setUserMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [language, setLanguage] = useState('english');

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
    // Reset chat history when language changes
    setChatHistory([]);
  };

  const handleSendMessage = (e) => {
    e.preventDefault();

    if (!userMessage) return;

    // Store user message in chat history
    setChatHistory((prev) => [...prev, { user: userMessage }]);

    // Process the message
    const lowerCaseMessage = userMessage.toLowerCase();
    const response = processMessage(lowerCaseMessage);
    
    // Store response in chat history
    setChatHistory((prev) => [...prev, { bot: response }]);
    setUserMessage('');
  };

  const processMessage = (message) => {
    // Handle greeting
    if (message.includes("hi") || message.includes("hello")) {
      return languages[language].greeting;
    }

    // Check for medical questions
    const medicalTerms = Object.keys(medicalQA[language]);
    for (let term of medicalTerms) {
      if (message.includes(term)) {
        const { remedies, medications } = medicalQA[language][term];
        const response = languages[language].recommendRemedies + '\n' + remedies.join('\n') + 
                         '\n\n' + languages[language].recommendMedication + '\n' + medications.join('\n');
        return response;
      }
    }

    // If no known terms found
    return languages[language].notFound;
  };

  return (
    <div className="chatbot">
      <h1>{languages[language].welcome}</h1>
      <select value={language} onChange={handleLanguageChange}>
        <option value="english">English</option>
        <option value="hindi">Hindi</option>
        <option value="marathi">Marathi</option>
      </select>
      <div className="chat-container">
        <div className="chat-history">
          {chatHistory.map((entry, index) => (
            <div key={index} className={entry.user ? "user-message" : "bot-message"}>
              {entry.user ? entry.user : entry.bot}
            </div>
          ))}
        </div>
        <form onSubmit={handleSendMessage}>
          <input
            type="text"
            placeholder={languages[language].placeholder}
            value={userMessage}
            onChange={(e) => setUserMessage(e.target.value)}
          />
          <button type="submit">Send</button>
        </form>
      </div>
      <div className="disclaimer">{languages[language].disclaimer}</div>
      <button onClick={() => setChatHistory([])}>{languages[language].clearChat}</button>
    </div>
  );
};

export default Chatbot;
