// Chatbot.js
import React, { useEffect, useState } from 'react';

const Chatbot = ({ isVisible, onClose }) => {
  useEffect(() => {
    if (isVisible) {
      // Load the Botpress script
      const script = document.createElement('script');
      script.src = "https://cdn.botpress.cloud/webchat/v2.1/inject.js";
      script.async = true;
      document.body.appendChild(script);

      // Load the Botpress config
      const configScript = document.createElement('script');
      configScript.src = "https://mediafiles.botpress.cloud/b5b86abe-9d91-4709-8def-c664a4064617/webchat/v2.1/config.js";
      configScript.async = true;
      document.body.appendChild(configScript);

      return () => {
        // Clean up scripts on component unmount
        document.body.removeChild(script);
        document.body.removeChild(configScript);
      };
    }
  }, [isVisible]);

  return isVisible ? (
    <div id="bp-webchat">
      <iframe
        title="Chatbot"
        src="https://cdn.botpress.cloud/webchat/v2.1/shareable.html?botId=b5b86abe-9d91-4709-8def-c664a4064617&embed=1"
        style={{ position: 'fixed', bottom: 0, right: 0, width: '350px', height: '500px', border: 'none' }}
        onClick={onClose}
      ></iframe>
    </div>
  ) : null;
};

export default Chatbot;
