import React, { useState } from 'react';
// import "./Login.css"

export default function Login() {
  const [loginEmail, setLoginEmail] = useState('');
  const [loginPassword, setLoginPassword] = useState('');
  const [signupName, setSignupName] = useState('');
  const [signupEmail, setSignupEmail] = useState('');
  const [signupPassword1, setSignupPassword1] = useState('');
  const [signupPassword2, setSignupPassword2] = useState('');
  const [loginOption, setLoginOption] = useState('');
  
  // Password Validation Function
  const validatePassword = (password) => {
    const regex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    return regex.test(password);
  };

  // Signup Function
  const handleSignup = () => {
    if (!validatePassword(signupPassword1)) {
      alert("Password should contain at least 1 capital letter, 1 small letter, 1 number, 1 special character, and be at least 8 characters long.");
      return;
    }

    if (signupPassword1 !== signupPassword2) {
      alert("Passwords do not match.");
      return;
    }

    // Store signup details in localStorage
    localStorage.setItem('nameC', signupName);
    localStorage.setItem('emailC', signupEmail);
    localStorage.setItem('pass1C', signupPassword1);
    localStorage.setItem('pass2C', signupPassword2);
    alert("You Are Successfully Signed Up. Please Go to Log In.");
  };

  // Login Function
  const handleLogin = () => {
    const storedEmail = localStorage.getItem('emailC');
    const storedPassword = localStorage.getItem('pass2C');

    if (loginEmail === storedEmail && loginPassword === storedPassword) {
      switch (loginOption) {
        case 'Admin':
          alert('Login successful as Admin');
          window.location.replace('admin.html');
          break;
        case 'HOD':
          alert('Login successful as HOD');
          window.location.replace('hod.html');
          break;
        case 'Student':
          alert('Student page is under development');
          window.location.replace('student.html');
          break;
        case 'Company':
          alert('Company page is under development');
          window.location.replace('company.html');
          break;
        default:
          alert('Select a valid login option.');
      }
    } else {
      alert("Login failed. Incorrect email or password.");
    }
  };

  return (
    <div className="container">
      {/* Login Form */}
      <div className="login form">
        <h3>Login</h3>
        <form>
          <select onChange={(e) => setLoginOption(e.target.value)} value={loginOption}>
            <option value="">Select login</option>
            <option value="Admin">Admin</option>
            <option value="Student">Student</option>
            <option value="HOD">HOD</option>
            <option value="Company">Company</option>
          </select>
          <input
            type="text"
            placeholder="Enter your email"
            value={loginEmail}
            onChange={(e) => setLoginEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Enter your password"
            value={loginPassword}
            onChange={(e) => setLoginPassword(e.target.value)}
          />
          <a href="#">Forgot password?</a>
          <input type="button" className="button" onClick={handleLogin} value="Login" />
        </form>
        <div className="signup">
          <span>Don't have an account? <label>Signup</label></span>
        </div>
      </div>

      {/* Signup Form */}
      <div className="registration form">
        <h3>Signup</h3>
        <form>
          <input
            type="text"
            placeholder="Enter your Name"
            value={signupName}
            onChange={(e) => setSignupName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter your email"
            value={signupEmail}
            onChange={(e) => setSignupEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Create a password"
            value={signupPassword1}
            onChange={(e) => setSignupPassword1(e.target.value)}
          />
          <input
            type="password"
            placeholder="Confirm your password"
            value={signupPassword2}
            onChange={(e) => setSignupPassword2(e.target.value)}
          />
          <input type="button" className="button" onClick={handleSignup} value="Signup" />
        </form>
        <div className="signup">
          <span>Already have an account? <label>Login</label></span>
        </div>
      </div>
    </div>
  );
}
