import React, { useContext } from "react";
import { Pie } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";
import { Box, Typography, Paper, List, ListItem, ListItemText } from "@mui/material";
import { PrescriptionContext }  from "./contexts/PrescriptionContext.js"; 
import Prescription from "./Prescription";

ChartJS.register(ArcElement, Tooltip, Legend);

const AshaDashboard = () => {
  const [prescription, setPrescription] = useState(null);
  useEffect(() => {
    let data = window.localStorage.getItem("prescription");
    if(!data) return;
    data = JSON
    setPrescription(data)
  }, []);

  const diseaseData = {
    labels: ["Fever", "Cough", "Headache", "Stomach Pain", "Other"],
    datasets: [
      {
        data: [30, 20, 15, 10, 25], 
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#4BC0C0", "#9966FF"],
      },
    ],
  };

  const total = diseaseData.datasets[0].data.reduce((sum, value) => sum + value, 0);
  const percentages = diseaseData.datasets[0].data.map(
    (value) => ((value / total) * 100).toFixed(2) + "%"
  );

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h4" gutterBottom>
        Asha Worker Dashboard
      </Typography>

      <Paper sx={{ padding: 3, marginBottom: 3 }}>
        <Typography variant="h6" gutterBottom>
          Disease Distribution
        </Typography>
        <Box sx={{ display: "flex", alignItems: "center", gap: 4 }}>
          <Box sx={{ width: "300px", height: "300px" }}>
            <Pie data={diseaseData} />
          </Box>

          <Box>
            <Typography variant="h6" gutterBottom>
              Disease Percentages
            </Typography>
            <List>
              {diseaseData.labels.map((label, index) => (
                <ListItem key={index}>
                  <ListItemText primary={`${label}: ${percentages[index]}`} />
                </ListItem>
              ))}
            </List>
          </Box>
        </Box>
      </Paper>

      <Paper sx={{ padding: 3 }}>
        <Typography variant="h6" gutterBottom>
          Latest Prescriptions
        </Typography>
        <List>
          {!prescription ? (
            <Typography>No prescriptions yet</Typography>
          ) : (
            <div>
            <Prescription symptoms={prescription.currentSymptom} remedies={prescription.currentRemedies} medications={prescription.currentMedication} />
          </div>
          )}
        </List>
      </Paper>
    </Box>
  );
};

export default AshaDashboard;
