const MEDICONNECTChatbot = () => {
  const [messages, setMessages] = useState([])
  const [input, setInput] = useState("")
  const [language, setLanguage] = useState("english")
  const [currentSymptom, setCurrentSymptom] = useState(null)
  const [conversationStage, setConversationStage] = useState("initial")

  const findSymptom = (question) => {
    const lowerQuestion = question.toLowerCase()
    const qaSet = medicalQA[language]
    for (const key of Object.keys(qaSet)) {
      if (lowerQuestion.includes(key.toLowerCase())) {
        return key
      }
    }
    return null
  }


  const getResponse = (symptom, duration, tookMedicine, medicineTaken) => {
    const { remedies, medications } = medicalQA[language][symptom];
    let response = `You've been experiencing ${symptom} for ${duration}. ${languages[language].recommendRemedies}\n\n`;
    response += remedies.map((remedy, index) => `${index + 1}. ${remedy}`).join("\n");

       if (!tookMedicine) {
      response += `\n\n${languages[language].recommendMedication}\n\n`;
      response += medications.map((med, index) => `${index + 1}. ${med}`).join("\n");
    } else if (medicineTaken && medications.includes(medicineTaken)) {
      response += `\n\n${languages[language].correctMedication.replace("{0}", medicineTaken)}`;
    } else if (medicineTaken) {
      response += `\n\n${languages[language].incorrectMedication.replace("{0}", medicineTaken)}\n\n`;
      response += medications.map((med, index) => `${index + 1}. ${med}`).join("\n");
    }

    return response;
  };

  const handleSendMessage = () => {
    if (input.trim()) {
      const newMessages = [...messages, { text: input, sender: "user" }];
      setMessages(newMessages);
      const symptom = findSymptom(input);

      if (symptom) {
        setCurrentSymptom(symptom);
        const symptomResponse = `${languages[language].askDuration}`;
        setMessages([...newMessages, { text: symptomResponse, sender: "bot" }]);
        setConversationStage("duration");
      } else {
        setMessages([...newMessages, { text: languages[language].notFound, sender: "bot" }]);
      }
      setInput("");
    }
  };

  const handleResponseFlow = (userInput) => {
    if (conversationStage === "duration") {
      const duration = userInput;
      setMessages([...messages, { text: duration, sender: "user" }]);
      setMessages([...messages, { text: languages[language].askMedicine, sender: "bot" }]);
      setConversationStage("medication");
    } else if (conversationStage === "medication") {
      const tookMedicine = userInput.toLowerCase() === "yes";
      setMessages([...messages, { text: userInput, sender: "user" }]);
      if (tookMedicine) {
        setMessages([...messages, { text: languages[language].askWhichMedicine, sender: "bot" }]);
        setConversationStage("medicineName");
      } else {
        const response = getResponse(currentSymptom, "some time", false, null);
        setMessages([...messages, { text: response, sender: "bot" }]);
        setConversationStage("initial");
        setCurrentSymptom(null);
      }
    } else if (conversationStage === "medicineName") {
      const medicineTaken = userInput;
      setMessages([...messages, { text: userInput, sender: "user" }]);
      const response = getResponse(currentSymptom, "some time", true, medicineTaken);
      setMessages([...messages, { text: response, sender: "bot" }]);
      setConversationStage("initial");
      setCurrentSymptom(null);
    }
  };

  return (
    <div className="chatbot">
      <div className="messages">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <input
        type="text"
        placeholder={languages[language].placeholder}
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
      />
      <button onClick={handleSendMessage}>
        <Send />
      </button>
      <button onClick={() => setMessages([])}>
        <Trash2 /> {languages[language].clearChat}
      </button>
    </div>
  );
};

export default MEDICONNECTChatbot;
