import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css'; // Ensure you have the correct CSS file

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [passwordError, setPasswordError] = useState(false);
  const [showPassword, setShowPassword] = useState(false); 
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    if (username === 'Mediconnect' && password === 'Mediconnect') {
      setPasswordError(false);
      window.localStorage.setItem("user", "true")
      window.location.href="/chatbot"
    } else {
      setPasswordError(true);
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <div className="login-container">
      <div className="login-box"> {/* White box container */}
        <h2>Login</h2>
        <form onSubmit={handleLogin}>
          <label>Username</label>
          <input
            type="text"
            value={username}
            placeholder="Enter your username"
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <label>Password</label>
          <div className="password-container">
            <input
              type={showPassword ? 'text' : 'password'} 
              value={password}
              placeholder="Enter your password"
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button
              type="button"
              className="toggle-password"
              onClick={togglePasswordVisibility}
              aria-label="Toggle Password Visibility"
            >
              {showPassword ? 'üëÅÔ∏è' : 'üôà'} 
            </button>
          </div>
          {passwordError && <p className="error-message">Invalid credentials. Please try again.</p>}
          <button type="submit">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
